    [TestClass]
    public class PersonTest : UnitTest
    {
        [TestMethod]
        public void ConstructorShouldNotAllowNullName()
        {
            var expected = "The name cannot be null.";
            Assert.ThrowsIfArgumentNull( ( string name ) => new Person( name ) ).Verify( e => e.Message == expected, "The actual message does not equal the expected message." );
        }
    }

