    using System.Collections.Specialized;

    [TestClass]
    public class PersonTest : UnitTest
    {
        [TestMethod]
        public void AddShouldInsertElementIntoCollection()
        {
            var target = new ObservableCollection<int>();
            Assert.AddChangedCollection( target, 1 );
        }
    }

